package HariKrishna;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// arithmatic operators
				int a=8;
				int b=15;
				System.out.println(a+b);
				System.out.println(a-b);
				System.out.println(a*b);
				System.out.println(a/b);
				System.out.println(a%b);
				System.out.println();
				
				
				// assignment operators
				int num=40;
				System.out.println(num+=25);
				System.out.println(num-=10);
				System.out.println(num*=5);
				System.out.println(num/=20);
				System.out.println(num%=10);
				System.out.println();
				
				
				//relational operators
				int x=10;
				int y =20;
				
				System.out.println(x==y);// equal to
				System.out.println(x!=y);// not equal
				System.out.println(x<y);// less than
				System.out.println(x>y);// greater than
				System.out.println(x<=y);// less than or equal to
				System.out.println(x>=y);//greater than or equal to
				System.out.println();
				
				// logical operators
				int s=10;
				System.out.println(s==10 && s<15);//true
				System.out.println(s!=15 || s<10);//true
				System.out.println(!(s==10));//false
				System.out.println();
				
				
				// incremental operator
				int n=10;
				++n;
				System.out.println(n);
				
		      // decremental operator
				int e=10;
				--e;
				System.out.println(e);
				int X=5;
				++X;
				System.out.println();
				
				
				
	}
			
	}


